circuit.qc:
OPENQASM 3.0;
qreg q[2];
creg c[2];
h q[0];
cx q[0], q[1];
measure q -> c;

OPENQASM 3.0;
qreg q[3];
creg c[3];
h q[0];
cx q[0], q[1];
cx q[1], q[2];
measure q -> c;

OPENQASM 3.0;
qreg q[3];
creg c[3];
x q[0];
h q[1];
cu1(pi/3) q[1], q[0];
h q[2];
cu1(pi/3) q[2], q[0];
cu1(pi/3) q[2], q[1];
measure q -> c;

OPENQASM 3.0;
qreg q[4];
creg c[4];

// Initialize with non-uniform state
x q[0];
x q[2];

// QFT implementation
h q[0];
cp(pi/2) q[1],q[0];
cp(pi/4) q[2],q[0];
cp(pi/8) q[3],q[0];
h q[1];
cp(pi/2) q[2],q[1];
cp(pi/4) q[3],q[1];
h q[2];
cp(pi/2) q[3],q[2];
h q[3];

// Swap qubits to get the correct output order
swap q[0],q[3];
swap q[1],q[2];

measure q -> c;

OPENQASM 3.0;
qreg q[3];
creg c[3];

// Initialize in superposition
h q[0];
h q[1];
h q[2];

// Oracle for marking state |101>
x q[0];
x q[2];
cz q[0],q[1];
cz q[1],q[2];
x q[0];
x q[2];

// Diffusion operator
h q[0];
h q[1];
h q[2];
x q[0];
x q[1];
x q[2];
h q[2];
ccx q[0],q[1],q[2];
h q[2];
x q[0];
x q[1];
x q[2];
h q[0];
h q[1];
h q[2];

measure q -> c;

OPENQASM 3.0;
qreg q[2];
creg c[2];
h q[0];
cx q[0], q[1];

OPENQASM 3.0;
qreg q[2];
creg c[2];
h q[0]
cx q[0], q[1];
measure q -> c;

OPENQASM 3.0;
qreg q[2];
creg c[2];
h q[0];
cx q[0], q[3];
measure q -> c;

OPENQASM 3.0;
qreg q[3];
creg c[2];

// Prepare the state to teleport (qubit 0)
rx(pi/4) q[0];

// Create Bell pair between qubits 1 and 2
h q[1];
cx q[1], q[2];

// Bell measurement between qubits 0 and 1
cx q[0], q[1];
h q[0];
measure q[0] -> c[0];
measure q[1] -> c[1];

// Apply corrections to qubit 2 based on measurements
if(c==1) x q[2];
if(c==2) z q[2];
if(c==3) { z q[2]; x q[2]; }

// Measure the final state of qubit 2
creg c2[1];
measure q[2] -> c2[0];

OPENQASM 3.0;
qreg q[12];
creg c[12];

// Create superposition
h q[0];
h q[1];
h q[2];
h q[3];
h q[4];
h q[5];
h q[6];
h q[7];
h q[8];
h q[9];
h q[10];
h q[11];

// Create entanglement chain
cx q[0], q[1];
cx q[1], q[2];
cx q[2], q[3];
cx q[3], q[4];
cx q[4], q[5];
cx q[5], q[6];
cx q[6], q[7];
cx q[7], q[8];
cx q[8], q[9];
cx q[9], q[10];
cx q[10], q[11];

// Cross-entanglement
cx q[0], q[6];
cx q[1], q[7];
cx q[2], q[8];
cx q[3], q[9];
cx q[4], q[10];
cx q[5], q[11];

// More operations
h q[0];
h q[2];
h q[4];
h q[6];
h q[8];
h q[10];

measure q -> c;
